CREATE SCHEMA IF NOT EXISTS disney DEFAULT CHARACTER SET latin1 ;
USE disney ;

CREATE TABLE IF NOT EXISTS disney.disney_plus_shows (
     imdb_id  VARCHAR(10),
     title  VARCHAR(62),
     plot  VARCHAR(312),
     type  VARCHAR(7),
     rated  VARCHAR(9),
     year  VARCHAR(9),
     released_at  VARCHAR(9),
     added_at  DATETIME,
     runtime  VARCHAR(7),
     genre  VARCHAR(95),
     director  VARCHAR(176),
     writer  VARCHAR(1032),
     actors  VARCHAR(77),
     language  VARCHAR(166),
     country  VARCHAR(127),
     awards  VARCHAR(67),
     metascore  VARCHAR(3),
     imdb_rating  VARCHAR(3),
     imdb_votes  VARCHAR(9) 
);

